import React from 'react';
import s from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {Redirect} from "react-router-dom";

const MessageForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field component={'input'} name={'messageText'} value={props.state.newMessageText}/>
            </div>
            <div>
                <button>Add message</button>
            </div>
        </form>
    )
}
const Dialogs = (props) => {
    let dialogElements = props.state.dialogs.map((d) => <DialogItem name={d.name} id={d.id} key={d.id} ava={d.ava}/>)
    let messageElements = props.state.messages.map((m) => <Message message={m.message} key={m.id}/>)

    let addMessage = () => {
        props.addDialog();
    };
    let onMessageChange = (e) => {
        let text = e.target.value;
        props.updateNewMessageText(text);
    };

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogElements}
            </div>
            <div className={s.messages}>
                {messageElements}
                <div>
                    <div>
                        <textarea onChange={onMessageChange} value={props.state.newMessageText}/>
                    </div>
                    <div>
                        <button onClick={addMessage}>Add message</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Dialogs;
