import React from 'react';
import {connect} from "react-redux";
import {
    followActionCreator,
    setUsersActionCreator,
    unfollowActionCreator,
    setCurrentPageActionCreator,
    setTotalUsersCountActionCreator,
    setOnNumberChangeActionCreator, setSearchNumberActionCreator
} from "../../redux/usersReducer";
import * as axios from "axios";
import Users from "./Users";

class UsersAPIComponent extends React.Component {
    /*constructor(props) {
        super(props);
        }
    }*/
    componentDidMount() {
        if (this.props.users.length == 0) {
            axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
                this.props.setUsers(response.data.items)
                this.props.setTotalUsersCount(response.data.totalCount);
            });
        }
    }

    onPageChanged = (pageNumber) => {
        this.props.setCurrentPage(pageNumber);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        });
    }
    numder=1;
    onNumberChange = (e) => {
        this.number = e.target.value;
        this.props.onNumberChange(number);
    };
    searchNumber = () => {
        this.props.searchNumber();
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.numder}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        });
    };


    render() {
        return  <Users
            totalUsersCount={this.props.totalUsersCount}
            pageSize={this.props.pageSize}
            currentPage={this.props.currentPage}
            onPageChanged={this.onPageChanged}
            searchNumber={this.searchNumber}
            onNumberChange={this.onNumberChange}
            numberChange={this.props.numberChange}
            users={this.props.users}
            unFollow={this.props.unFollow}
            follow={this.props.follow}
        />
    }
}

let mapStateToProps=(state)=>{
    return{
        totalUsersCount: state.usersPage.totalUsersCount,
        pageSize: state.usersPage.pageSize,
        users: state.usersPage.users,
        currentPage: state.usersPage.currentPage,
        numberChange:state.usersPage.numberChange,
    }
}

let mapDispatchToProps=(dispatch)=>{
    debugger
    return{
        follow:(userId)=>{dispatch(followActionCreator(userId))},
        unFollow:(userId)=>{dispatch(unfollowActionCreator(userId))},
        setUsers:(users)=>{dispatch(setUsersActionCreator(users))},
        setCurrentPage:(currentPage)=>{dispatch(setCurrentPageActionCreator(currentPage))},
        setTotalUsersCount:(totalCount)=>{dispatch(setTotalUsersCountActionCreator(totalCount))},
        onNumberChange:(number)=>{dispatch(setOnNumberChangeActionCreator(number))},
        searchNumber:()=>{dispatch(setSearchNumberActionCreator())},
    }
}
const UsersContainer=connect(mapStateToProps, mapDispatchToProps)(UsersAPIComponent);

export default UsersContainer;
