import React from 'react';
import s from './Users.module.css';
import userPhoto from "../../asets/images/instami-avatarka-v-instagram-9.png";
import {NavLink} from "react-router-dom";
import {followAPI} from "../../api/api";
import {deleteUser} from "../../redux/usersReducer";


const Users = (props) => {

    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }
    let mafs = () => {
        for (let i = 1; i < pages.length; i++) {
            if (props.currentPage == 1) return <div>
                <span className={s.selectedPage} onClick={() => {
                    props.onPageChanged(i);
                }}> {i} </span>
                <span className={props.currentPage + 1 === i && s.selectedPage} onClick={() => {
                    props.onPageChanged(i + 1);
                }}> {i + 1} </span>
                <span>...</span>
                <span className={pages.length === i && s.selectedPage} onClick={() => {
                    props.onPageChanged(pages.length);
                }}>{pages.length}
                </span>
            </div>
            if (props.currentPage == i) return <div><span className={props.currentPage - 1 === i && s.selectedPage}
                                                          onClick={() => {
                                                              props.onPageChanged(i - 1);
                                                          }}> {i - 1} </span>
                <span className={s.selectedPage} onClick={() => {
                    props.onPageChanged(i);
                }}> {i} </span>
                <span className={props.currentPage + 1 === i && s.selectedPage} onClick={() => {
                    props.onPageChanged(i + 1);
                }}> {i + 1} </span>
                <span>...</span>
                <span className={pages.length === i && s.selectedPage} onClick={() => {
                    props.onPageChanged(pages.length);
                }}>{pages.length}
                                                            </span></div>
            if (props.currentPage == pages.length) return <div><span onClick={() => {
                props.onPageChanged(1);
            }}> {1} </span>
                <span>...</span>
                <span onClick={() => {
                    props.onPageChanged(pages.length - 1);
                }}> {pages.length - 1} </span>
                <span className={s.selectedPage} onClick={() => {
                    props.onPageChanged(pages.length);
                }}> {pages.length}
                                                            </span></div>
        }
    }
    return (
        <div>
            <div>
                Поиск по сайту
                {mafs()}
                Введите номер страницы
                <div>
                    <textarea onChange={props.onNumberChange} value={props.numberChange}/>
                    <button onClick={props.searchNumber}>Поиск страницы</button>
                </div>
            </div>
            {props.users.map((u) =>
                <div className={s.margin}>
                    <div className={s.wrapper}>
                        <div className={s.photo}>
                            <NavLink to={'/profile/' + u.id}>
                                <img src={u.photos.large != null ? u.photos.large : userPhoto} className={s.userPhoto}/>
                            </NavLink>
                        </div>
                        <div className={s.name}>{u.userId}</div>
                        <div className={s.city}>{"props.location.city"}</div>
                        <div className={s.status}>{u.status != null ? u.status : "userStatus"}</div>
                        <div className={s.button}>
                            {u.followed
                                ? <button disabled={props.followingInProgress.some(id=>id==u.id)} onClick={() => {
                                    props.deleteUser(u.id)}}>Отписаться</button>
                                : <button disabled={props.followingInProgress.some(id=>id==u.id)} onClick={() => {
                                    props.postUser (u.id)}}>Подписаться</button>
                            }
                        </div>
                        <div className={s.country}>{"props.location.country"}</div>
                    </div>
                </div>)
            }
        </div>
    )
}

export default Users;
