import React from 'react';
import s from './Users.module.css';
import User from "./User/Users";
import * as axios from "axios";
import userPhoto from "../../asets/images/instami-avatarka-v-instagram-9.png";

class Users extends React.Component {
    constructor(props) {
        super(props);
        if (this.props.users.length == 0) {
            axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response => {
                this.props.setUsers(response.data.items)
            });
        }
    }

    user = this.props.users.map((u) => <User fullName={u.name}
                                             followed={u.followed}
                                             key={u.id}
                                             ava={u.photos.large}
                                             status={u.status}
                                             location={u.location}
                                             unFollow={() => {
                                                 this.props.unFollow(u.id)
                                             }}
                                             follow={() => {
                                                 debugger
                                                 this.props.follow(u.id)
                                             }}/>)

    render() {
        return (
            this.props.users.map(/*(u) => <User fullName={u.name}
                                              followed={u.followed}
                                              key={u.id}
                                              ava={u.photos.large}
                                              status={u.status}
                                              location={u.location}
                                              unFollow={() => {
                                                  this.props.unFollow(u.id)
                                              }}
                                              follow={() => {
                                                  debugger
                                                  this.props.follow(u.id)
                                              }}/>)*/
            <div className={s.margin}>
                <div className={s.wrapper}>
                    <div className={s.photo}><img src={this.props.ava !=null? this.props.ava: userPhoto } className={s.userPhoto}/></div>
                    <div className={s.name}>{this.props.fullName}</div>
                    <div className={s.city}>{"props.location.city"}</div>
                    <div className={s.button}>
                        {this.props.followed
                            ? <button onClick={() => {
                                this.props.unFollow(this.props.id)
                            }}>Отписаться</button>
                            : <button onClick={() => {
                                debugger
                                this.props.follow(this.props.id)
                            }}>Подписаться</button>
                        }
                    </div>
                    <div className={s.status}>{this.props.status}</div>
                    <div className={s.country}>{"props.location.country"}</div>
                </div>
            </div>
        )
        )
    }
}

export default Users;
