import React from "react";
import s from './FormControls.module.css';
import {requiredField} from "../../../utils/validators/validators";
import {Field} from "redux-form";


const FormControl = ({input, meta, ...props}) => {
    const hasError = meta.touched && meta.error
    return (
        <div>
            <div>
                <textarea {...input} {...props} className={hasError ? s.formControlError : ""}/>
            </div>
            {hasError && <span className={s.error}>{meta.error}</span>}
        </div>
    )
}


const Textarea = ({input, meta, ...props}) => {
    const hasError = meta.touched && meta.error
    return (
        <div>
            <div>
                <textarea {...input} {...props} className={hasError ? s.formControlError : ""}/>
            </div>
            {hasError && <span className={s.error}>{meta.error}</span>}
        </div>
    )
}
const Input = ({input, meta, ...props}) => {
    const hasError = meta.touched && meta.error
    return (
        <div>
            <div>
                <input {...input} {...props} className={hasError ? s.formControlError : ""}/>
            </div>
            {hasError && <span className={s.error}>{meta.error}</span>}
        </div>
    )
}

const CreateField = (placeholder, name, validate, component, type, text) => {
    return (
        <div>
            <Field placeholder={placeholder}
                   component={component}
                   name={name}
                   validate={validate}
                   type={type}
            />
        </div>
    )
}

export {Textarea, Input, CreateField}
