import React from 'react';
import Sidebarfriends from "./Sidebarfriends/Sidebarfriends";
import {addThreeFriendsActionCreator} from "../../../redux/sidebarReducer";
import Sidebar from "./Sidebar";

const SidebarContainer = (props) => {
    let state =props.store.getState();
    let addFriends = () => {
        props.store.dispatch(addThreeFriendsActionCreator())
    };
    window.onload=addFriends
    let threefriends = props.state.map((d) => <Sidebarfriends name={d.name} ava={d.ava}/>)
    return (
        <Sidebar threefriends={state.sidebar.friends}/>
    )
}

export default SidebarContainer;
