import {authAPI, profileAPI} from "../api/api";

const SET_USER_DATA = "SET-USER-DATA";
const SET_USER_SMALL_PHOTO = "SET-USER-SMALL-PHOTO";
const GET_ID= "GET-ID"

let initialState = {
    id: null,
    email: null,
    login: null,
    isAuth: false,
    smallPhoto: null
};

export const authReducer = (state = initialState, action) => {
    switch (action.type) {

        case SET_USER_DATA:
            return {
                ...state,
                ...action.data,
                isAuth: true,
            }
        case SET_USER_SMALL_PHOTO:
            return {
                ...state,
                smallPhoto: action.smallPhoto,
            }
        case GET_ID:
            return state.id
        default:
            return state;
    }
}


export const setAuthUserData = (userId, email, login) => {return {type: SET_USER_DATA, data:{userId, email, login}}};
export const setUserSmallPhoto=(smallPhoto)=>{return{type:SET_USER_SMALL_PHOTO,smallPhoto}};
const getId=()=>{return{type:GET_ID}}
export const authMe = () => {
    return (dispatch) => {
        authAPI.authMe().then(data => {
            if (data.resultCode == 0) {
                let {id, login, email} = data.data;
                dispatch(setAuthUserData(id, email, login));
            }
        })
            .then(response => {
                profileAPI.getUser(2).then(data => {
                    dispatch(setUserSmallPhoto(data.photos.small));
                })
            })
    }
}
