const ProfileStatusWithHooks = (props) => {
    return (
        <div>
            {true &&
            <div>
                <input onChange={onStatusChange} autoFocus={true} value={""}></input>
            </div>}
            {false &&
            <div>
                <span onClick={() => {
                activateEditMode()
                }}>{"---"}</span>
            </div>
            }
        </div>
    )
}
export default PProfileStatusWithHooks;
