    const Paginator = (props) => {
        let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
        let pages = [];
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i)
        }
        //alert(pages.length)
       //let startOfBlock=1;
        //let endOfBlock=10;
        let someBlock = pages.filter(item => item <= endOfBlock && item >= startOfBlock);
        let [startOfBlock, setStartOfBlock] = useState(startOfBlock=0)
        let [endOfBlock, setEndOfBlock] = useState(endOfBlock=10)

        useEffect(()=>{
            setStartOfBlock(props.startOfBlock)
        },[props.startOfBlock])

      /*  useEffect(()=>{
            setEndOfBlock(props.endOfBlock)
        },[props.endOfBlock])
*/
        return (
            <div>
                {startOfBlock!=1 && <span>Previous</span>}
                {someBlock.map((d) => <span onClick={() => {props.onPageChanged(d)}}> {d} </span>)}
                {endOfBlock!=pages.length && <span>Next</span>}
            </div>
        )
    }
    export default Paginator;
