import React from 'react';
import s from './ProfileInfo.module.css';
import Preloader from "../../common/Preloader/Preloader";
import facebook from "../../../asets/social_icons/Social Icons by Dreamstale (6).png";
import website from "../../../asets/social_icons/Social Icons by Dreamstale (1).png";
import vk from "../../../asets/social_icons/vk.png"
import twitter from "../../../asets/social_icons/Social Icons by Dreamstale (5).png";
import instagram from "../../../asets/social_icons/Social Icons by Dreamstale (33).png";
import youtube from "../../../asets/social_icons/Social Icons by Dreamstale (35).png";
import github from "../../../asets/social_icons/25231.svg";
import DialogItem from "../../Dialogs/DialogItem/DialogItem";
import ProfileStatus from "../../ProfileStatus/ProfileStatus";
import userPhoto from "../../../asets/images/instami-avatarka-v-instagram-9.png"
import ProfileStatusWithHooks from "../../ProfileStatus/ProfileStatusWithHooks";


const ProfileInfo = (props) => {
    let links = () => {
        let method2 = JSON.parse(JSON.stringify(props.profile.contacts));
        for (let [key, value] of Object.entries(props.profile.contacts)) {
            if (value === 'null') return (<div>s</div>)
            else {
                return (<a href={value}>{key}</a>)
            }
            //alert(`${key} ${value}`); // name:John, затем age:30
        }
    }
    if (props.profile == null || props.profile == undefined) {
        return <Preloader/>
    }

    return <div>
        {/*<div>
            <img
                src='https://images.pexels.com/photos/248797/pexels-photo-248797.jpeg?auto=compress&cs=tinysrgb&h=350'/>
        </div>*/}
        <div className={s.descriptionBlock}>
            <img src={props.profile.photos.large != null ? props.profile.photos.large : userPhoto}
                 className={s.userPhoto}/>
            <ProfileStatusWithHooks status={props.status} updateStatus={props.updateStatus}/> {/*{props.profile.aboutMe}*/}

            <div id="menu">
                <div>
                    {/*{links()}*/}
                </div>
                <a href={`http://` + `${props.profile.contacts.facebook}`}><img src={facebook}/></a>
                <a href={`http://` + `${props.profile.contacts.website}`}><img src={website}/></a>
                <a href={`http://` + `${props.profile.contacts.vk}`}><img src={vk}/></a>
                <a href={props.profile.contacts.twitter}><img src={twitter}/></a>
                <a href={`http://` + `${props.profile.contacts.instagram}`}><img src={instagram}/></a>
                <a href={`http://` + `${props.profile.contacts.youtube}`}><img src={youtube}/></a>
                <a href={`http://` + `${props.profile.contacts.github}`}><img src={github}/></a>
            </div>
        </div>
    </div>
}

export default ProfileInfo;
