import React from 'react';
import Sidebarfriends from "./Sidebarfriends/Sidebarfriends";
import {addThreeFriendsActionCreator} from "../../../redux/sidebarReducer";
import Sidebar from "./Sidebar";
import {connect} from "react-redux";
import {addPostActionCreator, updateNewPostTextActionCreator} from "../../../redux/profileReducer";



/*const SidebarContainer = (props) => {
    return (
        <StoreContext.Consumer>{
            (store) => {
                let state = store.getState();
                let addFriends = () => {
                    store.dispatch(addThreeFriendsActionCreator())
                };
                window.onload = addFriends
                return (
                    <Sidebar threefriends={state.sidebar.friends}/>
                )
            }
        }
        </StoreContext.Consumer>
    )
}*/
let mapStateToProps=(state)=>{
    return{
        threefriends: state.sidebar.friends,
    }
}

let mapDispatchToProps=(dispatch)=>{
    return{
        updateNewPostText:(text)=>{dispatch(updateNewPostTextActionCreator(text))},
        newPostText:()=>{dispatch(addPostActionCreator())},
    }
}
const SidebarContainer=connect(mapStateToProps, mapDispatchToProps)(Sidebar);

export default SidebarContainer;
