const ADD_THREE_FRIENDS = "ADD-THREE-FRIENDS";
export const sidebarReducer = (state, action) => {
    switch (action.type) {
        case ADD_THREE_FRIENDS: {
           // state.sidebar.friends = JSON.parse(JSON.stringify(state.dialogsPage.dialogs))

            function getRandomArbitrary(min, max) {
                let rand = min - 0.5 + Math.random() * (max - min + 1);
                return Math.round(rand);
            }

            for (let i = state.sidebar.friends.length; i > 3; i--) {
                state.sidebar.friends.splice(getRandomArbitrary(0, state.sidebar.friends.length - 1), 1);
            }
            return state;
        }
        default: return state;
    }
}

export const addThreeFriendsActionCreator = () => {
    return {
        type: ADD_THREE_FRIENDS
    }
}
