import {authAPI, profileAPI} from "../api/api";
import {stopSubmit} from "redux-form";

const SET_INITIALIZED= "SET-INITIALIZED";


let initialState = {
    initialized: false,
};

export const appReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_INITIALIZED:
            return {
                ...state,
                initialized: true,
            }
        default:
            return state;
    }
}


export const setAuthUserData = (userId, email, login, isAuth) => {
    return {type: SET_USER_DATA, data: {userId, email, login, isAuth}}
};
export const setUserSmallPhoto = (smallPhoto) => {
    return {type: SET_USER_SMALL_PHOTO, smallPhoto}
};
export const authMe = () => {
    return (dispatch) => {
        let id;
        let login;
        let email;
        authAPI.authMe().then(data => {
            if (data.resultCode == 0) {
                id = data.data.id;
                login = data.data.login;
                email = data.data.email;
                dispatch(setAuthUserData(id, email, login, true));
            }
        }).then(response => {
            profileAPI.getUser(id).then(data => {
                dispatch(setUserSmallPhoto(data.photos.small));
            })
        })
    }
};
export const LoginAC = (email, password, rememberMe) => (dispatch) => {
    authAPI.login(email, password, rememberMe).then(data => {
        if (data.resultCode == 0) dispatch(authMe());
        else {
            let message = data.messages.length>0 ? data.messages[0] : "some error";
            dispatch(stopSubmit('login', {_error: message}));
        }
    });
};
export const LogoutAC = () => (dispatch) => {
    authAPI.logout().then(data => {
        if (data.resultCode == 0) dispatch(setAuthUserData(null, null, null, false));
    });
};
