import React from 'react';
import s from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
const addMessageActionCreator=()=> {
    return {
        type: "ADD-MESSAGE"
    }
}
const updateNewMessageTextActionCreator=(text)=>{
    return{
        type: 'UPDATE-NEW-MESSAGE-TEXT', newText: text}
}


const Dialogs = (props) => {
    let dialogElements = props.state.dialogs.map((d) => <DialogItem name={d.name} id={d.id} ava={d.ava}/>)
    let messageElements = props.state.messages.map((m) => <Message message={m.message}/>)

    let newMessage = React.createRef();

    let addMessage = () => {
        props.dispatch(addMessageActionCreator())
    };
    let onMessageChange = () => {
        let text = newMessage.current.value;
        props.dispatch(updateNewMessageTextActionCreator(text))
        //props.updateNewMessageText(text);
    };

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogElements}
            </div>
            <div className={s.messages}>
                {messageElements}
                <div>
                    <div>
                        <textarea onChange={onMessageChange} ref={newMessage} value={props.state.newMessageText}/>
                    </div>
                    <div>
                        <button onClick={addMessage}>Add message</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Dialogs;
