import React from 'react';
import s from './Users.module.css';
import * as axios from "axios";
import userPhoto from "../../asets/images/instami-avatarka-v-instagram-9.png";

class Users extends React.Component {
    /*constructor(props) {
        super(props);
        }
    }*/
    componentDidMount() {
        if (this.props.users.length == 0) {
            axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
                this.props.setUsers(response.data.items)
                this.props.setTotalUsersCount(response.data.totalCount);
            });
        }

    }

    onPageChanged = (pageNumber) => {
        this.props.setCurrentPage(pageNumber);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        });
    }


    render() {

        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);

        let pages = [];
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i)
        }
        let mafs=()=>{
            for(let i=1;i<pages.length;i++)
            {
                if (this.props.currentPage==1) return <div>
                                        <span className={this.props.currentPage === i && s.selectedPage} onClick={()=>{this.onPageChanged(i);}}> {i} </span>
                                        <span className={this.props.currentPage+1 === i && s.selectedPage} onClick={()=>{this.onPageChanged(i+1);}}> {i+1} </span>
                                        <span>...</span>
                                        <span className={pages.length === i && s.selectedPage} onClick={()=>{this.onPageChanged(pages.length);}}>{pages.length}
                                        </span>
                                </div>
                if (this.props.currentPage==i) return <div> <span className={this.props.currentPage-1 === i && s.selectedPage} onClick={()=>{this.onPageChanged(i-1);}}> {i-1} </span>
                                                            <span className={this.props.currentPage === i && s.selectedPage} onClick={()=>{this.onPageChanged(i);}}> {i} </span>
                                                            <span className={this.props.currentPage+1 === i && s.selectedPage} onClick={()=>{this.onPageChanged(i+1);}}> {i+1} </span>
                                                            <span>...</span>
                                                            <span className={pages.length === i && s.selectedPage} onClick={()=>{this.onPageChanged(pages.length);}}>{pages.length}
                                                            </span></div>
                if (this.props.currentPage==pages.length) return <div> <span  onClick={()=>{this.onPageChanged(1);}}> {1} </span>
                                                             <span>...</span>
                                                             <span onClick={()=>{this.onPageChanged(pages.length-1);}}> {pages.length-1} </span>
                                                            <span className={s.selectedPage} onClick={()=>{this.onPageChanged(pages.length);}}> {pages.length}
                                                            </span></div>
            }
        }
        let onNumberChange = (e) => {
            let number = e.target.value;
            this.props.onNumberChange(number);
        };
        let searchNumber = () => {
            this.props.searchNumber();
        };



        return <div>
            <div>
                Поиск по сайту
                {/*{pages.map(p=> {
                    return <span className={this.props.currentPage === p && s.selectedPage}
                    onClick={()=>{this.onPageChanged(p);}}>{this.props.currentPage===p||this.props.currentPage+1||this.props.currentPage+2||this.props.totalUsersCount/this.props.pageSize===p ? p:""}</span>
                    })}*/}
                {mafs()}
                Введите номер страницы
                <div>
                    <textarea onChange={onNumberChange} value={this.props.numberChange}/>
                    <button onClick={searchNumber}>Поиск страницы</button>
                </div>
            </div>
            {this.props.users.map((u) =>
                <div className={s.margin}>
                    <div className={s.wrapper}>
                        <div className={s.photo}><img src={u.photos.large != null ? u.photos.large : userPhoto}
                                                      className={s.userPhoto}/></div>
                        <div className={s.name}>{u.name}</div>
                        <div className={s.city}>{"props.location.city"}</div>
                        <div className={s.status}>{u.status != null ? u.status : "userStatus"}</div>
                        <div className={s.button}>
                            {u.followed
                                ? <button onClick={() => {
                                    this.props.unFollow(u.id)
                                }}>Отписаться</button>
                                : <button onClick={() => {
                                    debugger
                                    this.props.follow(u.id)
                                }}>Подписаться</button>
                            }
                        </div>

                        <div className={s.country}>{"props.location.country"}</div>
                    </div>
                </div>)
            }
        </div>
    }
}

export default Users;
