import React from 'react';
import s from './ProfileInfo.module.css';
import Preloader from "../../common/Preloader/Preloader";
import facebook from "../../../asets/social_icons/Social Icons by Dreamstale (6).png"

const ProfileInfo = (props) => {
    if(props.pofile==null||props.pofile==undefined){
        return <Preloader/>
    }

    return <div>
        <div >
            <img src='https://images.pexels.com/photos/248797/pexels-photo-248797.jpeg?auto=compress&cs=tinysrgb&h=350' />
        </div>
        <div className={s.descriptionBlock}>
            <img src={props.pofile.photos.large}/>
            <div>{props.pofile.aboutMe}</div>
            <a href={props.pofile.contacts.facebook}><img src={facebook} width="50" height="50"/></a>
        </div>
  </div>
}

export default ProfileInfo;
