        return Math.round(Math.random() * (max - min) + min);
    }

    let first = getRandomArbitrary(0, props.state.length);
    let second = getRandomArbitrary(0, props.state.length);
    if (second == first) second = getRandomArbitrary(0, props.state.length);
    let third = getRandomArbitrary(0, props.state.length);
    if ((third == first) || (third == second)) third = getRandomArbitrary(0, props.state.length);
