     let first = getRandomArbitrary(0, props.state.length);
     if (arr.indexOf(first)==-1) arr.push(first);
     for(let i=0; i)
    alert(getRandomArbitrary(0, props.state.length));
 */
    /*let threefriends = props.state.map((d) => <Sidebarfriends name={d.name} id={d.id} ava={d.ava}/>)*/
    return (
        <div>
            FRIENDS
            {/*{threefriends}*/}
            <div className="wrapper">
                <Sidebarfriends name={props.state[first].name} id={props.state[first].id} ava={props.state[first].ava}/>
                <Sidebarfriends name={props.state[second].name} id={props.state[second].id}
                                ava={props.state[second].ava}/>
                <Sidebarfriends name={props.state[third].name} id={props.state[third].id} ava={props.state[third].ava}/>
            </div>
        </div>
    )
}

export default Sidebar;
