import React from 'react';
import s from './User.module.css';


const User = (props) => {
    return (
        <div className={s.wrapper}>
            <div className={s.sidebar}><img src={props.ava} className={s.userPhoto}/></div>
            <div className={s.header}>{props.fullName}</div>
            <div className={s.city}>{props.location.city}</div>
            <div className={s.footer}>
            {props.followed
                ? <button onClick={()=>{props.unfollow(props.id)}}>Отписаться</button>
                : <button onClick={()=>{props.follow(props.id)}}>Подписаться</button>
            }
            </div>
            <div className={s.content}>{props.status}</div>
            <div className={s.country}>{props.location.country}</div>


        </div>
    )
}

export default User;
