import React from 'react';
import s from './Sidebar.module.css';
import Sidebarfriends from "./Sidebarfriends/Sidebarfriends";

const Sidebar = (props) => {
    function getRandomArbitrary(min, max) {
        return Math.round(Math.random() * (max - min) + min);
    }
    let arr= props;

    for (let i=arr.state.length; i>3; i--){
        arr.state.splice(getRandomArbitrary(0, arr.state.length-1), 1);
    }
    alert(props.state.length)
    let threefriends = arr.state.map((d) => <Sidebarfriends name={d.name} ava={d.ava}/>)
    return (
        <div>
            FRIENDS
            <div className={s.wrapper}>
                {threefriends}
            </div>
        </div>
    )
}

export default Sidebar;
