import React from 'react';
import s from './Sidebar.module.css';
import Sidebarfriends from "./Sidebarfriends/Sidebarfriends";

const Sidebar = (props) => {
   /* function getRandomArbitrary(min, max) {
        let rand = min - 0.5 + Math.random() * (max - min + 1);
        return Math.round(rand);
    }

    let arr = props.state;
    for (let i = arr.length; i > 3; i--) {
        arr.splice(getRandomArbitrary(0, arr.length - 1), 1);
    }
*/
    //alert(props.dispatch({index:'GET-THREE-FRIENDS'}))
    let arr=props.dispatch({type:'GET-THREE-FRIENDS'});
    let threefriends = arr.map((d) => <Sidebarfriends name={d.name} ava={d.ava}/>)
    return (
        <div>
            FRIENDS
            <div className={s.wrapper}>
                {threefriends}
            </div>
        </div>
    )
}

export default Sidebar;
