const FOLLOW = "FOLLOW";
const UNFOLLOW = "UNFOLLOW";
const SET_USERS='SET-USERS'

let initialState = {
    users: [
        {
            id: 1,
            ava: 'http://zhaba.ru/storage-10667/images-4356/7da28183c1ca335727f6033007c24c3f_74356.gif',
            followed: true,
            fullName: 'Andry',
            status: 'Сижу кушаю',
            lacation: {city: 'Dnipro', country: 'Ukraine'}
        },
        {
            id: 2,
            ava: 'http://zhaba.ru/storage-10667/images-4356/2ec52d3e31cc84526b5c1b8d332bb5f2_74356.gif',
            followed: false,
            fullName: 'Viktoriya',
            status: 'на море',
            lacation: {city: 'Poltava', country: 'Ukraine'}
        },
        {
            id: 3,
            ava: 'http://zhaba.ru/storage-10667/images-4356/54c86b4b33e968bb15eeea983d72a850_74356.gif',
            followed: true,
            fullName: 'Natalia',
            status: 'в астрале',
            lacation: {city: 'Dnipro', country: 'Ukraine'}
        },
        {
            id: 4,
            ava: 'http://zhaba.ru/storage-10667/images-4356/05f88dffdf9a406fe9b0bee9df3160a5_74356.gif',
            followed: false,
            fullName: 'Dmitry',
            status: 'я Сенсей',
            lacation: {city: 'Minsk', country: 'Belarus'}
        },
    ],
    newPostText: "",
};

export const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW: {
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return {...u, followed: true}
                    }
                    return u
                }),
            }
        }
        case UNFOLLOW: {
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return {...u, followed: false}
                    }
                    return u
                }),
            }
        }
        case SET_USERS:{
            return {
                ...state,
                users: [...state.users, ...action.users]
            }
        }
        default:
            return state;
    }
    return state;
}
export const followActionCreator = (userID) => {
    return {
        type: FOLLOW,
        userID,
    }
}
export const unfollowActionCreator = (userID) => {
    return {
        type: UNFOLLOW,
        userID,
    }
}
export const setUsersActionCreator=(users)=>{type: SET_USERS, users}
