import React from 'react';
import Header from "./Header";
import * as axios from "axios";
import {connect} from "react-redux";
import {setAuthUserData, setUserSmallPhoto} from "../../redux/authReducer";
import {authAPI, profileAPI} from "../../api/api";

class HeaderContainer extends React.Component {
    componentDidMount() {
        //this.props.toggleIsFetching(true);
        authAPI.authMe().then(data => {
                if (data.resultCode == 0) {
                    let {id, login, email} = data.data;
                    this.props.setAuthUserData(id, email, login)
                }
            })
            .then(response => {
                profileAPI.getUser(this.props.id).then(data => {
                    debugger;
                    this.props.setUserSmallPhoto(data.photos.small);
                })
            });
    }
    render() {
        return <Header {...this.props}/>
    }
}

const mapStatetoProps = (state) => ({
    isAuth: state.auth.isAuth,
    login: state.auth.login,
    id: state.auth.userId,
});
export default connect(mapStatetoProps, {setAuthUserData, setUserSmallPhoto})(HeaderContainer);
