import React from 'react';
import {connect} from "react-redux";
import {
    follow,
    setUsers,
    unFollow,
    setCurrentPage,
    setTotalUsersCount,
    onNumberChange,
    searchNumber,
    toggleIsFetching, toggleIsFetchingProgress
} from "../../redux/usersReducer";
import * as axios from "axios";
import Users from "./Users";
import Preloader from "../common/Preloader/Preloader";
import {userAPI} from "../../api/api";

class UsersAPIComponent extends React.Component {
    /*constructor(props) {
        super(props);
        }
    }*/
    componentDidMount() {
        this.props.toggleIsFetching(true);
        userAPI.getUsers(this.props.currentPage, this.props.pageSize).then(data => {
            this.props.toggleIsFetching(false);
            this.props.setUsers(data.items);
            this.props.setTotalUsersCount(data.totalCount);
        });
    }

    onPageChanged = (pageNumber) => {
        this.props.toggleIsFetching(true);
        this.props.setCurrentPage(pageNumber);
        userAPI.getUsers(pageNumber, this.props.pageSize).then(data => {
            this.props.toggleIsFetching(false);
            this.props.setUsers(data.items);
        });
    }
    onNumberChange = (e) => {
        let number = e.target.value;
        this.props.onNumberChange(number);
    };
    searchNumber = () => {
        this.props.searchNumber();
        this.props.toggleIsFetching(true);
        userAPI.getUsers(this.props.numberChange, this.props.pageSize).then(data => {
            this.props.toggleIsFetching(false);
            this.props.setUsers(data.items);
        });
    };


    render() {
        return <>
            {this.props.isFetching ? <Preloader/> : null}
            <Users
                totalUsersCount={this.props.totalUsersCount}
                pageSize={this.props.pageSize}
                currentPage={this.props.currentPage}
                onPageChanged={this.onPageChanged}
                searchNumber={this.searchNumber}
                onNumberChange={this.onNumberChange}
                numberChange={this.props.numberChange}
                users={this.props.users}
                unFollow={this.props.unFollow}
                follow={this.props.follow}
                followingInProgress={this.props.followingInProgress}
                toggleIsFetchingProgress={this.props.toggleIsFetchingProgress}
            />
        </>
    }
}

let mapStateToProps = (state) => {
    return {
        totalUsersCount: state.usersPage.totalUsersCount,
        pageSize: state.usersPage.pageSize,
        users: state.usersPage.users,
        currentPage: state.usersPage.currentPage,
        numberChange: state.usersPage.numberChange,
        isFetching: state.usersPage.isFetching,
        followingInProgress: state.usersPage.followingInProgress,
    }
}

const UsersContainer = connect(mapStateToProps, {
    follow, unFollow, setUsers, setCurrentPage, setTotalUsersCount,
    onNumberChange, searchNumber, toggleIsFetching, toggleIsFetchingProgress,
    getUsersThunkCreator,
})(UsersAPIComponent);

export default UsersContainer;
