import s from "./Users.module.css";
import React from "react";


export let Paginator = (props) => {
    let mafs = () => {
        for (let i = 1; i < props.pages.length; i++) {
            if (props.currentPage == 1) return <div>
            <span className={s.selectedPage} onClick={() => {
                props.onPageChanged(i);
            }}> {i} </span>
                <span className={props.currentPage + 1 === i && s.selectedPage} onClick={() => {
                    props.onPageChanged(i + 1);
                }}> {i + 1} </span>
                <span>...</span>
                <span className={props.pages.length === i && s.selectedPage} onClick={() => {
                    props.onPageChanged(props.pages.length);
                }}>{props.pages.length}
            </span>
            </div>
            if (props.currentPage == i) return <div><span className={props.currentPage - 1 === i && s.selectedPage}
                                                          onClick={() => {
                                                              props.onPageChanged(i - 1);
                                                          }}> {i - 1} </span>
                <span className={s.selectedPage} onClick={() => {
                    props.onPageChanged(i);
                }}> {i} </span>
                <span className={props.currentPage + 1 === i && s.selectedPage} onClick={() => {
                    props.onPageChanged(i + 1);
                }}> {i + 1} </span>
                <span>...</span>
                <span className={props.pages.length === i && s.selectedPage} onClick={() => {
                    props.onPageChanged(props.pages.length);
                }}>{props.pages.length}
            </span></div>

            if (props.currentPage == props.pages.length-1) return <div><span onClick={() => {
                props.onPageChanged(1);
            }}> {1} </span>
                <span>...</span>
                <span onClick={() => {
                    props.onPageChanged(props.pages.length - 3);
                }}> {props.pages.length - 2} </span>
                <span className={s.selectedPage} onClick={() => {
                    props.onPageChanged(props.pages.length - 2);
                }}> {props.pages.length - 1} </span>
                <span onClick={() => {
                    props.onPageChanged(props.pages.length);
                }}> {props.pages.length}
            </span></div>

            if (props.currentPage == props.pages.length) return <div><span onClick={() => {
                props.onPageChanged(1);
            }}> {1} </span>
                <span>...</span>
                <span onClick={() => {
                    props.onPageChanged(props.pages.length - 1);
                }}> {props.pages.length - 1} </span>
                <span className={s.selectedPage} onClick={() => {
                    props.onPageChanged(props.pages.length);
                }}> {props.pages.length}
            </span></div>
        }
    }
    return (
        <div>{mafs()}
        </div>
    )
}
