import React from 'react';
import s from './ProfileInfo.module.css';
import Preloader from "../../common/Preloader/Preloader";
import facebook from "../../../asets/social_icons/Social Icons by Dreamstale (6).png";
import website from "../../../asets/social_icons/Social Icons by Dreamstale (1).png";
import vk from "../../../asets/social_icons/vk.png"
import twitter from "../../../asets/social_icons/Social Icons by Dreamstale (5).png";
import instagram from "../../../asets/social_icons/Social Icons by Dreamstale (33).png";

const ProfileInfo = (props) => {
    if(props.pofile==null||props.pofile==undefined){
        return <Preloader/>
    }

    return <div>
        <div >
            <img src='https://images.pexels.com/photos/248797/pexels-photo-248797.jpeg?auto=compress&cs=tinysrgb&h=350' />
        </div>
        <div className={s.descriptionBlock}>
            <img src={props.pofile.photos.large}/>
            <div>{props.pofile.aboutMe}</div>
            <a href={`http://`+`${props.pofile.contacts.facebook}`}><img src={facebook}/></a>
            <a href={`http://`+`${props.pofile.contacts.website}`}><img src={website}/></a>
            <a href={`http://`+`${props.pofile.contacts.vk}`}><img src={vk}/></a>
            <a href={props.pofile.contacts.twitter}><img src={twitter}/></a>
            <a href={`http://`+`${props.pofile.contacts.instagram}`}><img src={instagram}/></a>
        </div>
  </div>
}

export default ProfileInfo;
