import React from 'react';
import s from './Users.module.css';
import User from "./User/Users";
import * as axios from "axios";


const UsersOLD = (props) => {
    /*let u1 = [
        {
            id: 1,
            ava: 'http://zhaba.ru/storage-10667/images-4356/7da28183c1ca335727f6033007c24c3f_74356.gif',
            followed: true,
            fullName: 'Andry',
            status: 'Сижу кушаю',
            location: {city: 'Dnipro', country: 'Ukraine'}
        },
        {
            id: 2,
            ava: 'http://zhaba.ru/storage-10667/images-4356/2ec52d3e31cc84526b5c1b8d332bb5f2_74356.gif',
            followed: false,
            fullName: 'Viktoriya',
            status: 'на море',
            location: {city: 'Poltava', country: 'Ukraine'}
        },
        {
            id: 3,
            ava: 'http://zhaba.ru/storage-10667/images-4356/54c86b4b33e968bb15eeea983d72a850_74356.gif',
            followed: true,
            fullName: 'Natalia',
            status: 'в астрале',
            location: {city: 'Dnipro', country: 'Ukraine'}
        },
        {
            id: 4,
            ava: 'http://zhaba.ru/storage-10667/images-4356/05f88dffdf9a406fe9b0bee9df3160a5_74356.gif',
            followed: false,
            fullName: 'Dmitry',
            status: 'я Сенсей',
            location: {city: 'Minsk', country: 'Belarus'}
        },
    ]*/
    let getUsers = () => {
        if (props.users.length == 0) {
            axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response => {
                debugger;
                props.setUsers(response.data.items)
            });
        }
    }


// props.setUsers(u1);}
    let user = props.users.map((u) => <User fullName={u.name}
                                            followed={u.followed}
                                            key={u.id}
                                            ava={u.photos.large}
                                            status={u.status}
                                            location={u.location}
                                            unfollow={() => {
                                                props.unfollow(u.id)
                                            }}
                                            follow={() => {
                                                props.follow(u.id)
                                            }}/>)
    return (
        <div className={s.margin}>
            <button onClick={getUsers}>GetUsers</button>
            {user}
        </div>
    )
}

export default UsersOLD;
