import React, {useState} from 'react';
import s from './ProfileStatus.module.css';

const ProfileStatusWithHooks = (props) => {

   let [editMode, setEditMode]=useState({editMode:false, status: props.status})
    let activateMode=()=>{
        setEditMode(true);
    }
    let deactivateMode=()=>{
        setEditMode(false);
       // props.updateStatus(this.state.status)
    }

    return (
        <div>
            {!editMode &&
            <div>
                <span onClick={activateMode}> {props.status || '---'}</span>
            </div>
            }
            {editMode &&
            <div>
                <input onBlur={deactivateMode} autoFocus={true}/>
            </div>
            }
        </div>
    )
}
export default ProfileStatusWithHooks;
