import React from 'react';
import s from './Users.module.css';
import userPhoto from "../../asets/images/instami-avatarka-v-instagram-9.png";
import {NavLink} from "react-router-dom";
import {followAPI} from "../../api/api";
import {deleteUser} from "../../redux/usersReducer";
import {maxLengthCreator, requiredField} from "../../utils/validators/validators";
import {Field, reduxForm} from "redux-form";
import {Textarea} from "../common/FormControls/FormControls";
import {Paginator} from "../common/Paginator/Paginator";
import User from "./User";

const maxLength4 = maxLengthCreator(4)
const searchPage = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field component={Textarea} name={'searchPage'} validate={[requiredField, maxLength4]}
                       placeholder={'Введите страницу'}/>
            </div>
            <div>
                <button>Поиск страницы</button>
            </div>
        </form>
    )
}
const SearchPageForm = reduxForm({
    form: "searchPage"
})(searchPage);


const Users = (props) => {
    const onSubmit = (formData) => {
        props.onPageChanged(formData.searchPage)
    }

    return (
        <div>
            <div>
                Поиск по сайту
                <Paginator currentPage={props.currentPage}
                           onPageChanged={props.onPageChanged}
                           totalUsersCount={props.totalUsersCount}
                           pageSize={props.pageSize}
                />
                Введите номер страницы
                <SearchPageForm onSubmit={onSubmit}/>
            </div>
            {props.users.map((u) => <User key ={u.id} className={s.margin} user={u} followingInProgress={props.followingInProgress}/>
                /*<div className={s.margin}>
                <div className={s.wrapper}>
                <div className={s.photo}>
                <NavLink to={'/profile/' + u.id}>
                <img src={u.photos.large != null ? u.photos.large : userPhoto} className={s.userPhoto}/>
                </NavLink>
                </div>
                <div className={s.name}>{u.name}</div>
                <div className={s.city}>{u.id}</div>
                <div className={s.status}>{u.status != null ? u.status : "userStatus"}</div>
                <div className={s.button}>
                {u.followed
                    ? <button disabled={props.followingInProgress.some(id => id == u.id)} onClick={() => {
                        props.deleteUser(u.id)
                    }}>Отписаться</button>
                    : <button disabled={props.followingInProgress.some(id => id == u.id)} onClick={() => {
                        props.postUser(u.id)
                    }}>Подписаться</button>
                }
                </div>
                <div className={s.country}>{"props.location.country"}</div>
                </div>
                </div>*/)
            }
        </div>
    )
}

export default Users;
